INSERT INTO `publication` VALUES ('18', '1', '封面文章', '2016-12-26 19:11:37', '2017-01-08 20:16:13', '<p>\r\n	<span style=\"font-family:SimSun;color:#E53333;font-size:24px;line-height:1;\"><span style=\"font-family:Georgia, sans-serif;\"><span style=\"font-size:18px;\"><span style=\"color:#C0C0C0;\">&nbsp;</span></span></span><span style=\"font-family:Georgia, sans-serif;\"><span style=\"font-size:18px;\"><span style=\"color:#C0C0C0;\"></span><span style=\"line-height:2;color:#999999;\">灵巧操作是服务机器人实现多种智能化任务的基本途径，面对非结构化动态环境，其操作的灵巧程度及智能化取决于对环境的感知水平及学习能力。该研究方向将识别压力、剪切力等参数的柔性可拉伸触觉传感阵列与识别形貌、纹理等参数的柔性可拉伸微透镜成像阵列集成于机械手电子皮肤，制备不仅有触觉感知、滑觉感知（包括预知操作物体的摩擦力），也有深度学习力学及成像数据能力的机器人或智能假肢，从而提升机器人及假肢在具有自主性及适应性操作上的智能化。</span></span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;color:#E53333;font-size:24px;line-height:1;\"><span style=\"font-family:SimSun;color:#E53333;font-size:24px;line-height:1;\"></span><span style=\"font-family:SimSun;color:#E53333;font-size:24px;line-height:1;\"></span><br />\r\n</span> \r\n</p>');
